document.addEventListener("alpine:initializing",()=>{window.Alpine.data("navigationSortableContainer",({statePath:t})=>({statePath:t,sortable:null,init(){this.sortable=new Sortable(this.$el,{group:"nested",animation:150,fallbackOnBody:!0,swapThreshold:.5,draggable:"[data-sortable-item]",handle:"[data-sortable-handle]",onSort:()=>{this.sorted()}})},sorted(){this.$wire.sortNavigation(this.statePath,this.sortable.toArray())}}))});
